simple.prompt:
input_variables=['context', 'question'] template='Answer the users question based only on the following context:

<context>
{context}
</context>

Question: {question}
'

rewrite_prompt.prompt:
input_variables=['context', 'question'] template='Answer the users question based only on the following context:

<context>
{context}
</context>

Question: {question}
'

stepback_prompt.prompt:
input_variables=['normal_context', 'question', 'step_back_context'] template='Answer the users question based only on the following context and the background Context:

<context>
{normal_context}
</context>

<Background Context>
{step_back_context}
</Background Context>

Question: {question}
'
